mapper.select:
select * 
from funding
where funding_no = #{no}

mapper.update:
update funding_participation
set status = 'N'
where no = ${no}

update
	funding
set
	rate_plan_code = #{charge}
where
	funding_no = #{no}

update funding
content = #{content}, early_content = #{earlyContent}, start_date = #{startDate}
where funding_no = #{fundingNo}

update funding_reward
price = #{price}, title = #{title}, content = #{content}, shipping_price = #{shippingPrice}, limit_amount = #{limitAmount}, shipping_date = #{shippingDate}
where funding_no = #{fundingNo}

mapper.select:
select *
from funding
where funding_no = #{fundingNo}

select count(*) count
from funding_participation
where funding_no = #{fundingNo}

select *
from like_record
where member_no = #{memberNo} and funding_no = #{fundingNo}

mapper.update:
update like_record
set status = #{status, typeHandler=booleanYnTypeHandler}
where no = #{no}
