mapper.insert:
insert into funding( funding_no, writer_no ) values( seq_funding_no.nextval, ${writerNo}
)
select seq_funding_no.currval from dual

mapper.update:
update funding
content = ${content},
early_content = ${earlyContent},
start_date = ${startDate}
where funding_no = ${fundingNo}

mapper.select:
select count(*) count
from funding_participation
where funding_no = #{funding_no}
